export class UsersPageComponent implements OnInit {

  isLoading = true;
  isChangingPage = false;

  users:any[] = [];

  totalLength = 0;
  pageSize = 5;

  userForm = this.fb.group({
    email: ['',Validators.email],
    name: ['',Validators.required],
    isActive:['']
  });

  searchControl = new FormControl('');

  toggleForm = false;

