    <div fxFlex="45" fxFlexAlign="start">
      <app-school-form
        *ngIf="schoolToggleId || createMode"
        [schoolId]="schoolToggleId"
        [createMode]="createMode"
        (save)="refresh()">
      </app-school-form>
    </div>
  </div>
</div>

