      <mat-error *ngIf="managerForm.get('name')?.errors?.required">{{'fieldErrors.nameIsRequired'|translate}}</mat-error>
      <mat-error *ngIf="managerForm.get('name')?.errors?.pattern">{{'fieldErrors.namePattern'|translate}}</mat-error>
      <mat-error *ngIf="managerForm.get('name')?.errors?.minlength">{{'fieldErrors.nameMinLength'|translate}}</mat-error>
    </mat-form-field>
    <mat-form-field appearance="outline">
      <mat-label>{{'labels.school'|translate}}</mat-label>
      <input data-cy-input="school" type="text" placeholder="{{'placeholder.schoolName'| translate}}" matInput formControlName="school" [matAutocomplete]="auto">
      <mat-autocomplete #auto="matAutocomplete" [displayWith]="displaySchoolName">
        <mat-option [attr.data-cy-input-school-id]="school.id" *ngFor="let school of filteredSchools$ | async" [value]="school">
          {{school.name + ' | ' + school.identifier}}
        </mat-option>
      </mat-autocomplete>
    </mat-form-field>
    <mat-slide-toggle 
     formControlName="isActive"
     [attr.data-cy-input]="'isActive:' + managerForm.get('isActive')?.value"
     >{{'labels.active'|translate}}</mat-slide-toggle>
    <div  *ngIf="isLoading;then showLoader;else showButton"></div>
    <ng-template #showLoader>
      <mat-spinner diameter="50"></mat-spinner>
    </ng-template>
    <ng-template #showButton>
      <button 
      mat-raised-button [disabled]="managerForm.invalid" 
      data-cy-manager-button="save" 
      [attr.data-cy-is-disabled]="managerForm.invalid"  
      type="submit">{{(isEdit ? 'labels.save':'labels.create')|translate}}</button>
    </ng-template>
  </form>
</div>

import { Component, OnInit } from '@angular/core';
import {FormBuilder, FormControl, Validators} from "@angular/forms";
import {PageEvent} from "@angular/material/paginator";
import {ManagerService} from "../../services/manager.service";
import { of } from 'rxjs';
import Manager from 'src/app/shared/model/manager';

@Component({
  selector: 'app-manager-page',
  templateUrl: './manager-page.component.html',
  styleUrls: ['./manager-page.component.scss']
})
export class ManagerPageComponent implements OnInit {

  isLoading = true;
  isChangingPage = false;

  managers:any[] = [];

  totalLength = 0;
  pageSize = 5;

  filteredManagers$ = of([] as Manager[]);

  searchControl = new FormControl('');

  managerForm = this.fb.group({
    email: ['',Validators.email],
    name: ['',Validators.required],
    school: ['',Validators.required],
    isActive:['']
  });

  managerToggleId: number | null = null;

  createMode = true;

  constructor(private managerService: ManagerService, private fb: FormBuilder) {
    this.refresh();
  }

  ngOnInit(): void {

    this.managerForm.get('name')?.valueChanges.subscribe(value => {
      if(!value?.id){
        if (value === ""){
          this.refresh();
        } else {
          this.managerService.findManagerByNameSimilarity(value).subscribe (managers => {
            this.managers = managers;
          }
        );
        }
      }
    })
  }

  refresh(){
    this.managerService.getManagersUsers(this.pageSize,0).subscribe(response => {
      this.managers = response.content;
      this.totalLength = response.totalElements;
      this.isLoading = false;
    })
  }

  pageChange(pageEvent: PageEvent) {
    this.isChangingPage = true;
    this.managerService.getManagersUsers(pageEvent.pageSize,pageEvent.pageIndex).subscribe(response => {
      this.managers = response.content;
      this.pageSize = pageEvent.pageSize;
      this.totalLength = response.totalElements;
      this.isChangingPage = false;
    })
  }

  displayManagerName(manager: Manager){
    return manager && manager.name ? manager.name: '';
  }

}
<div fxLayout="column" fxLayoutAlign="center start">
  <div class="page-title" fxFlexAlign="start" fxLayoutAlign="center start">
    <button mat-icon-button routerLink="/admin/dashboard"><mat-icon>navigate_before</mat-icon></button>
    <h1 class="mat-display-1">{{'titles.managers'|translate}}</h1>
  </div>
  <div class="full-width" fxLayout="row" fxLayoutAlign="space-around  start">
    <div fxFlex="45">
      <mat-form-field class="full-width" appearance="outline">
        <form [formGroup] = "managerForm">
        <input type="tel" matInput placeholder="{{'placeholder.searchManager'| translate}}" [formControl]="searchControl" [matAutocomplete]="auto" formControlName="name">
        <mat-autocomplete #auto="matAutocomplete" [displayWith]="displayManagerName">
          <mat-option *ngFor="let manager of filteredManagers$ | async" [value]="manager">
            {{manager.name + ' | ' + manager.school.name}}
          </mat-option>
        </mat-autocomplete>
        </form>
        <mat-icon matSuffix>search</mat-icon>
      </mat-form-field>
      <a
        data-cy-manager="create"
        [ngClass]="'mat-elevation-z1 '+  (createMode?'toggled-tile':'tile')"
        fxLayout="row"
        fxFlexAlign="center center"
        fxLayoutAlign="start center"
        (click)="createMode=true;managerToggleId=null">
        <mat-icon color="primary">add</mat-icon>
        <p color="primary">{{'labels.createManager'|translate}}</p>
      </a>
      <div *ngIf="isLoading" fxLayout="row" fxLayoutAlign="center center">
        <mat-spinner></mat-spinner>
      </div>
      <a
        [attr.data-cy-edit-manager]="manager.id"
        [ngClass]="'mat-elevation-z1 ' + (managerToggleId===manager.id?'toggled-tile':'tile')"
        fxLayout="row"
        fxFlexAlign="center center"
        fxLayoutAlign="start center"
        *ngFor="let manager of managers"
        (click)="managerToggleId=manager.id === undefined ? null : manager.id;createMode=false">
        <mat-icon [ngClass]="manager.isActive?'primary':'disabled'">person</mat-icon>
        <p color="primary">{{manager.name}}</p>
      </a>
      <mat-paginator
        class="full-width"
        [length]="totalLength"
        [pageSize]="pageSize"
        [pageSizeOptions]="[5, 10, 25, 100]"
        (page)="pageChange($event)">
      </mat-paginator>
    </div>
    <div fxFlex="45" fxFlexAlign="start">
      <app-manager-form
        *ngIf="managerToggleId || createMode"
        [managerId]="managerToggleId"
        [createMode]="createMode"
        (save)="refresh()">
      </app-manager-form>
    </div>
  </div>
</div>

import { Injectable } from '@angular/core';
import { ActivatedRouteSnapshot, CanActivate, RouterStateSnapshot, UrlTree } from '@angular/router';
import { Observable } from 'rxjs';
import {AuthenticationService} from "../services/authentication.service";
import {map} from "rxjs/operators";

@Injectable({
  providedIn: 'root'
})
export class IsManagerGuard implements CanActivate {
  constructor(private auth: AuthenticationService) {}

  canActivate(
    route: ActivatedRouteSnapshot,
    state: RouterStateSnapshot): Observable<boolean | UrlTree> | Promise<boolean | UrlTree> | boolean | UrlTree {
    return this.auth
      .getRole()
      .pipe(
        map(role => role === 'MANAGER')
      );
  }


}

import { Injectable } from '@angular/core';
import {environment} from "../../../environments/environment";
import {take} from "rxjs/operators";
import {HttpClient} from "@angular/common/http";

@Injectable({
  providedIn: 'root'
})
export class AchievementsService {

  constructor(private http: HttpClient) { }

  getAchievements(pageSize: number, pageNumber: number) {
    return this.http.get<any>(`${environment.apiUrl}/api/achievements?size=${pageSize}&pageNumber=${pageNumber}`).pipe(take(1));
  }

  getAchievementById(achievementId: number) {
    return this.http.get<any>(`${environment.apiUrl}/api/achievements/${achievementId}`).pipe(take(1));
  }

  updateAchievement(achievementId: number | null, achievement: any) {
    return this.http.put<any>(`${environment.apiUrl}/api/achievements/${achievementId}`,achievement).pipe(take(1));
  }

  createAchievement({beforeAt,...rest}: any) {
    const formattedBeforeAt =  beforeAt?.format('YYYY-MM-DD kk:mm:ss');
    return this.http.post<any>(`${environment.apiUrl}/api/achievements`, {beforeAt: formattedBeforeAt,...rest}).pipe(take(1));
  }

  getStudentAchievements() {
    return this.http.get<any>(`${environment.apiUrl}/api/achievements/student`).pipe(take(1));
  }
}

