  qualifiersForm = this.fb.group({
    beforeAt: [''],
    earlierOf: [''],
    bestOf: [''],
    averageGradeGreaterOrEqualsThan: [''],
  });

  generalInfo = this.fb.group({
    name:  ['',Validators.required],
    description: ['', Validators.required],
    isActive: [true,Validators.required]
  })

  iconNameControl = this.fb.control('',Validators.required)

  classIdControl = this.fb.control('');
  activityIdControl = this.fb.control('');

  modalityChosenControl = new FormControl('',Validators.required);

  filteredClasses = [] as any[];
  activities:any[] = []
  isLoading = false;

  constructor(
    private route: ActivatedRoute,
    private fb: FormBuilder,
    private achievementService: AchievementsService,
    private schoolService: SchoolService,
    private snackbar: MatSnackBar,
    private translateService: TranslateService,
    private classService: ClassService,
    private activityService: ActivityService,
    private snackbarService: SnackbarService,
    private router: Router
  ) { }

  ngOnInit(): void {
    forkJoin(
      this.classService.getTeacherHimselfClasses(),
      this.activityService.getTeacherActivities()
    ).subscribe(([classes,activities]) => {
      this.filteredClasses = classes;
      this.activities = activities;
    })

    this.modalityChosenControl.valueChanges.subscribe(value => {
      if(value==='activity'){
        this.classIdControl.setValue('');
      }else{
        this.activityIdControl.setValue('');
      }
    })
  }

  ngOnChanges(changes: SimpleChanges){
    this.isEdit = !this.createMode;
    if(this.createMode){
      this.qualifiersForm.reset();
    }
    else{
      this.achievementService.getAchievementById(this.achievementId as number).subscribe( (value) => {
        const {id, name, description, isActive, iconName, activityId, classId,...qualifiers } = value;
        this.generalInfo.setValue({name,description,isActive});
        this.iconNameControl.setValue(iconName);
        this.qualifiersForm.setValue({...qualifiers})
        this.classIdControl.setValue(classId);
        this.activityIdControl.setValue(activityId);
        if(activityId){
          this.modalityChosenControl.setValue('activity')
        }else{
          this.modalityChosenControl.setValue('school-classes')
        }
      })
    }
  }

  updateAchievement(){
    this.isLoading = true;
    const achievement = {
      ...this.generalInfo.value,
      iconName: this.iconNameControl.value,
      classId: this.classIdControl.value,
      activityId: this.activityIdControl.value,
      ...this.qualifiersForm.value,
    }
    this.achievementService.updateAchievement(this.achievementId,achievement).subscribe(success => {
      this.snackbarService.showSnack('messages.achievementUpdated','labels.close')
      this.save.emit();
    }, error => {
      this.isLoading = false;
    })
  }

  registerAchievement(){
    this.isLoading = true;
    const achievement = {
      ...this.generalInfo.value,
      iconName: this.iconNameControl.value,
      classId: this.classIdControl.value,
      activityId: this.activityIdControl.value,
      ...this.qualifiersForm.value,
    }
    this.achievementService.createAchievement(achievement).subscribe(success => {
    this.snackbarService.showSnack('messages.achievementCreated','labels.close')
    this.save.emit();
    this.qualifiersForm.reset();
    this.generalInfo.reset();
    this.iconNameControl.reset();
    this.classIdControl.reset();
    this.activityIdControl.reset();
    this.modalityChosenControl.reset();
    this.qualifiersForm.reset();
    }, error => {
      this.isLoading = false;
      switch (error.status){
        case 409:
          break;
      }
    });
  }

  displaySchoolName(school: School){
    return school && school.name ? school.name: '';
  }

  selectIcon(icon: string) {
    this.iconNameControl.setValue(icon);
  }
}
<div class="default-padding full-width" fxLayout="column" fxLayoutAlign="center start">

  <h1 class="title">
    {{(isEdit?'labels.updateAchievement' : 'labels.createAchievement') |translate}}
  </h1>
  <mat-stepper orientation="vertical" [linear]="false " #stepper>
    <mat-step [stepControl]="generalInfo">
      <form [formGroup]="generalInfo" fxLayout="column">
        <ng-template  matStepLabel>{{'labels.generalInfo'|translate}}</ng-template>
        <mat-form-field
          fxFlex="45" appearance="outline">
          <mat-label>{{'labels.name'|translate}}</mat-label>
          <input
            data-cy-input="name"
            matInput
            type="text"
            formControlName="name"
            required>
          <mat-error *ngIf="qualifiersForm.get('name')?.errors?.required">{{'fieldErrors.nameIsRequired'|translate}}</mat-error>
        </mat-form-field>
        <mat-form-field
          fxFlex="45" appearance="outline">
          <mat-label>{{'labels.description'|translate}}</mat-label>
          <textarea
            data-cy-input="description"
            matInput
            type="text"
            formControlName="description"
            required>
          </textarea>
        </mat-form-field>
        <mat-slide-toggle formControlName="isActive">{{'labels.active'|translate}}</mat-slide-toggle>
        <div class="top-padding full-width" fxLayoutAlign="space-between end" fxLayout="column">
          <div fxFlex="10"></div>
          <button data-cy-button="goToStep2" 
          [attr.data-cy-is-disabled]="generalInfo.invalid" 
          mat-flat-button matStepperNext [disabled]="generalInfo.invalid">{{'labels.next'|translate}}</button>
        </div>
      </form>
    </mat-step>
    <mat-step [stepControl]="iconNameControl">
      <ng-template matStepLabel>{{'labels.chooseIcon'|translate}}</ng-template>
      <div fxFlex="100" fxLayout="row wrap" class="icon-picker">
        <div  *ngFor="let icon of materialIcons ">
          <button  (click)="selectIcon(icon)" mat-icon-button>
            <mat-icon [attr.data-cy-input]="materialIcons[0]" [ngClass]="iconNameControl?.value !== icon? 'disabled' : 'picked'">
              {{icon}}_filled
            </mat-icon>
          </button>
        </div>
      </div>
      <div fxLayout="row" fxLayoutAlign="space-between center" class="top-padding">
        <button  mat-raised-button color="primary" matStepperPrevious>
          {{'labels.previous'|translate}}
        </button>
        <button data-cy-button="goToStep3" mat-flat-button color="primary" [disabled]="iconNameControl.value == ''" matStepperNext>
          {{'labels.next'|translate}}
        </button>
      </div>
    </mat-step>
    <mat-step>
      <ng-template matStepLabel>{{'labels.modality'|translate}}</ng-template>
      <div fxLayout="row" fxLayoutAlign="space-between center">
        <mat-form-field fxFlex="45" appearance="outline">
          <mat-label>{{'labels.modality'|translate}}</mat-label>
          <mat-select data-cy-input="modalidade" [formControl]="modalityChosenControl" >
            <mat-option data-cy-option="activity" [value]="'activity'">
              {{'labels.byActivity'|translate}}
            </mat-option>
            <mat-option [value]="'school-classes'">
              {{'labels.byClass'|translate}}
            </mat-option>
          </mat-select>
        </mat-form-field>
        <mat-form-field *ngIf="modalityChosenControl?.value == 'school-classes'" fxFlex="45" appearance="outline">
          <mat-label>{{'labels.school-classes'|translate}}</mat-label>
          <mat-select [formControl]="classIdControl" >
            <mat-option *ngFor="let schoolClass of filteredClasses"  [value]="schoolClass.id">
              {{schoolClass.name}}
            </mat-option>
          </mat-select>
        </mat-form-field>
        <mat-form-field *ngIf="modalityChosenControl?.value == 'activity'" fxFlex="45" appearance="outline">
          <mat-label>{{'labels.activity'|translate}}</mat-label>
          <mat-select data-cy-activity="activity" [formControl]="activityIdControl">
            <mat-option [attr.data-cy-activity]="activity.id"*ngFor="let activity of activities" [value]="activity.id">
              {{activity.name}}
            </mat-option>
          </mat-select>
        </mat-form-field>
      </div>
      <div fxLayout="row" fxLayoutAlign="space-between center">
        <button mat-raised-button color="primary" matStepperPrevious>{{'labels.previous'|translate}}</button>
        <button data-cy-button="goToStep4" mat-flat-button color="primary" [disabled]="!(activityIdControl.value || classIdControl.value)"  matStepperNext>{{'labels.next'|translate}}</button>
      </div>
    </mat-step>
    <mat-step>
      <ng-template matStepLabel>{{'labels.qualifiers'|translate}}</ng-template>
      <form [formGroup]="qualifiersForm">
        <div *ngIf="classIdControl.value">
          <mat-form-field
            fxFlex="45" appearance="outline">
            <mat-label>{{'labels.averageGradeGreaterOrEqualsThan'|translate}}</mat-label>
            <input
              matInput
              type="number"
              formControlName="averageGradeGreaterOrEqualsThan">
          </mat-form-field>
        </div>
        <div *ngIf="activityIdControl.value" fxLayout="column">
          <mat-form-field fxFlex="100" appearance="outline">
            <mat-label>{{'labels.deliveryBeforeAt'|translate}}</mat-label>
            <input
              matInput
              [ngxMatDatetimePicker]="picker"
              placeholder="{{'placeholder.chooseDeliveryDate'|translate}}"
              formControlName="beforeAt">
            <mat-datepicker-toggle matSuffix [for]="$any(picker)"></mat-datepicker-toggle>
            <ngx-mat-datetime-picker
              #picker>
            </ngx-mat-datetime-picker>
          </mat-form-field>
          <mat-form-field
            fxFlex="45" appearance="outline">
            <mat-label>{{'labels.deliveryEarlierOf'|translate}}</mat-label>
            <input
              data-cy-input="earlierOf"
              matInput
              type="number"
              formControlName="earlierOf">
          </mat-form-field>
          <mat-form-field
            fxFlex="45" appearance="outline">
            <mat-label>{{'labels.deliveryBestOf'|translate}}</mat-label>
            <input
              matInput
              type="number"
              formControlName="bestOf">
          </mat-form-field>
        </div>
      </form>
      <div fxLayout="row" fxLayoutAlign="space-between center">
        <div fxFlex="10"></div>
        <button mat-flat-button color="primary"
        data-cy-achievement-button="save" 
        (click)="isEdit ? updateAchievement() : registerAchievement()" matStepperNext>{{(isEdit?'labels.save':'labels.createAchievement')|translate}}</button>
      </div>
    </mat-step>
  </mat-stepper>
</div>

import { Component, OnInit } from '@angular/core';
import {FormControl} from "@angular/forms";
import {ManagerService} from "../../../../admin/manager/services/manager.service";
import {PageEvent} from "@angular/material/paginator";
import {AchievementsService} from "../../../services/achievements.service";

@Component({
  selector: 'app-achievement-page',
  templateUrl: './achievement-page.component.html',
  styleUrls: ['./achievement-page.component.scss']
})
export class AchievementPageComponent implements OnInit {

  isLoading = true;
  isChangingPage = false;

  achievements:any[] = [];

  totalLength = 0;
  pageSize = 5;

  searchControl = new FormControl('');

  achievementToggleId: number | null = null;

  createMode = true;

  constructor(private achievementService: AchievementsService) {
    this.refresh();
  }

  ngOnInit(): void {
  }

  refresh(){
    this.achievementService.getAchievements(this.pageSize,0).subscribe(response => {
      this.achievements = response.content;
      this.totalLength = response.totalElements;
      this.isLoading = false;
    })
  }

  pageChange(pageEvent: PageEvent) {
    this.isChangingPage = true;
    this.achievementService.getAchievements(pageEvent.pageSize,pageEvent.pageIndex).subscribe(response => {
      this.achievements = response.content;
      this.pageSize = pageEvent.pageSize;
      this.totalLength = response.totalElements;
      this.isChangingPage = false;
    })
  }

}
<div fxLayout="column" fxLayoutAlign="center start">
  <div class="page-title" fxFlexAlign="start" fxLayoutAlign="center start">
    <button mat-icon-button routerLink="/teacher/dashboard"><mat-icon>navigate_before</mat-icon></button>
    <h1 class="mat-display-1">{{'titles.achievements'|translate}}</h1>
  </div>
  <div class="full-width" fxLayout="row" fxLayoutAlign="space-around  start">
    <div fxFlex="45">
      <mat-form-field class="full-width" appearance="outline">
        <input type="tel" matInput placeholder="{{'placeholder.searchAchievements'|translate}}" [formControl]="searchControl">
        <mat-icon matSuffix>search</mat-icon>
      </mat-form-field>
      <a
        data-cy-achievement="create"
        [ngClass]="'mat-elevation-z1 '+  (createMode?'toggled-tile':'tile')"
        fxLayout="row"
        fxFlexAlign="center center"
        fxLayoutAlign="start center"
        (click)="createMode=true;achievementToggleId=null">
        <mat-icon color="primary">add</mat-icon>
        <p color="primary">{{'labels.createAchievement'|translate}}</p>
      </a>
      <div *ngIf="isLoading" fxLayout="row" fxLayoutAlign="center center">
        <mat-spinner></mat-spinner>
      </div>
      <a
        [attr.data-cy-edit-achievement]="achievement.id"
        [ngClass]="'mat-elevation-z1 ' + (achievementToggleId===achievement.id?'toggled-tile':'tile')"
        fxLayout="row"
        fxFlexAlign="center center"
        fxLayoutAlign="start center"
        *ngFor="let achievement of achievements"
        (click)="achievementToggleId=achievement.id;createMode=false">
        <mat-icon [ngClass]="achievement.isActive?'primary':'disabled'">{{achievement.iconName}}</mat-icon>
