import { Injectable } from '@angular/core';
import {HttpClient} from "@angular/common/http";
import {TranslateService} from "@ngx-translate/core";
import {environment} from "../../../../environments/environment";
import {take} from "rxjs/operators";
import {MatSnackBar} from "@angular/material/snack-bar";
import {Router} from "@angular/router";
import { Observable } from 'rxjs';
import Teacher from 'src/app/shared/model/teacher';

@Injectable({
  providedIn: 'root'
})
export class TeacherService {

  isLoading = false;

  constructor(
    private http: HttpClient,
    private translateService: TranslateService,
    private snackbar: MatSnackBar,
    private router: Router
  ) { }

  registerTeacher(value: any) {
    const {school,...rest} = value;
    const language = this.translateService.currentLang;
    return this.http.post<any>(`${environment.apiUrl}/api/teachers`, {language,...rest});
  }

  getTeachersUsers(pageSize: number, pageNumber: number) {
    return this.http.get<any>(`${environment.apiUrl}/api/teachers?size=${pageSize}&pageNumber=${pageNumber}`).pipe(take(1));
  }

  getTeacherById(managerId: number) {
    return this.http.get<any>(`${environment.apiUrl}/api/teachers/${managerId}`).pipe(take(1));
  }

  updateTeacher(managerId: number | null, value: any) {
    this.isLoading = true;
    const {school,...rest} = value;
    const language = this.translateService.currentLang;
    return this.http.put<any>(`${environment.apiUrl}/api/teachers/${managerId}`, {language,...rest});
    this.isLoading = false;
  }

  findTeacherByNameSimilarity(value: string): Observable<Teacher[]> {
    return this.http.get<Teacher[]>(`${environment.apiUrl}/api/teachers/name/${value}`).pipe(take(1));
  }

  showSnackbar(message: string){
    this.snackbar.open(message,'Fechar');
  }

  findByEmailSimilarity(email: string) {
    return this.http.get<any[]>(`${environment.apiUrl}/api/teachers/email/${email}`).pipe(take(1));
  }
}

import {Component, EventEmitter, Input, OnInit, Output, SimpleChanges} from '@angular/core';
import {FormBuilder, Validators} from "@angular/forms";
import {of} from "rxjs";
import School from "../../../../shared/model/school";
import {ActivatedRoute, Router} from "@angular/router";
import {SchoolService} from "../../../../admin/schools/services/school.service";
import {MatSnackBar} from "@angular/material/snack-bar";
import {TranslateService} from "@ngx-translate/core";
import {TeacherService} from "../../services/teacher.service";


@Component({
  selector: 'app-teacher-form',
  templateUrl: './teacher-form.component.html',
  styleUrls: ['./teacher-form.component.scss']
})
export class TeacherFormComponent implements OnInit {

  isEdit = false;

  @Input() teacherId:number | null = null;
  @Input() createMode = true;

  @Output() save = new EventEmitter();
  alreadyRegisteredEmail = false;

  teacherForm = this.fb.group({
    email: ['',Validators.email],
    name: ['',Validators.required],
    isActive:['']
  });

  isLoading = false;
  filteredSchools$ = of([] as School[]);

  constructor(
    private route: ActivatedRoute,
    private fb: FormBuilder,
    private teacherService: TeacherService,
    private schoolService: SchoolService,
    private snackbar: MatSnackBar,
    private translateService: TranslateService,
    private router: Router
  ) { }

  ngOnInit(): void {
    this.teacherForm.get('school')?.valueChanges.subscribe(value => {
      if(!value?.id && value !== ""){
        this.filteredSchools$ = this.schoolService.findSchoolByNameSimilarity(value);
      }
    })
    this.route.params.subscribe(value => {
      if(value['id']){
        this.isEdit = true;
        this.teacherId = value['id'];
        this.teacherService.getTeacherById(this.teacherId as number).subscribe(({email,name,isActive})=>{
          this.teacherForm.setValue({email,name,isActive});
        });
      }
    })
  }

  ngOnChanges(changes: SimpleChanges){
    this.isEdit = !this.createMode;
    if(this.createMode){
      this.teacherForm.reset();
    }
    else{
      this.teacherService.getTeacherById(this.teacherId as number).subscribe( ({name,email,isActive}) => {
        this.teacherForm.setValue({name,email,isActive})
      })
    }
  }

  updateTeacher(){
    this.isLoading = true;
    this.teacherService.updateTeacher(this.teacherId,this.teacherForm.value).subscribe(success => {
      this.translateService.get('messages.managerUpdated').subscribe( translation => {
        this.isLoading = false;
        this.save.emit();
        this.snackbar.open(translation,'Fechar').afterDismissed();
      })
    }, error => {
      this.isLoading = false;
    })
  }

  registerTeacher(){
    this.isLoading = true;
    this.teacherService.registerTeacher(this.teacherForm.value).subscribe(success => {
      this.translateService.get('messages.managerCreated').subscribe( translation => {
        this.isLoading = false;
        this.save.emit();
        this.teacherForm.reset();
        this.snackbar.open(translation,'Fechar');
      })
    }, error => {
      this.isLoading = false;
      switch (error.status){
        case 409:
          this.translateService.get('messages.alreadyRegisteredIdentifier').subscribe(translation => {
            this.snackbar.open(translation, 'Fechar');
          })
          this.alreadyRegisteredEmail = true;
          break;
      }
    });
  }

  displaySchoolName(school: School){
    return school && school.name ? school.name: '';
  }

  showSnackbar(message: string){
    this.snackbar.open(message,'Fechar');
  }

}
<div class="default-padding full-width" fxLayout="column" fxLayoutAlign="center start">

  <h1 class="title">
    {{(isEdit?'titles.updateTeacher' : 'titles.registerTeacher') |translate}}
  </h1>

  <form fxLayout="row wrap" fxLayoutAlign="space-between" class="full-width" fxFlex="100" [formGroup]="teacherForm" (ngSubmit)="isEdit ? updateTeacher() : registerTeacher()">
    <mat-form-field
      fxFlex="45" appearance="outline">
      <mat-label>E-mail</mat-label>
      <input
       data-cy-input="email"
        matInput
        type="text"
        formControlName="email"
        placeholder="{{'placeholder.email'| translate}}"
        pattern="^[a-z0-9._%+-]+@[a-z0-9.-]+\.[a-z]{2,4}$"
        required>
      <mat-error *ngIf="alreadyRegisteredEmail">{{'fieldErrors.accountNotFound'|translate}}</mat-error>
      <mat-error *ngIf="teacherForm.get('email')?.errors?.required">{{'fieldErrors.emailIsRequired'|translate}}</mat-error>
      <mat-error *ngIf="teacherForm.get('email')?.errors?.pattern">{{'fieldErrors.emailPattern'|translate}}</mat-error>
    </mat-form-field>
    <mat-form-field
      fxFlex="45" appearance="outline">
      <mat-label>{{'labels.name'|translate}}</mat-label>
      <input
        data-cy-input="name"
        matInput
        type="text"
        formControlName="name"
        placeholder="{{'placeholder.teacherName'| translate}}"
        minlength="5"
        pattern="^([a-zA-Z--]|\s+)+$"
        required>
      <mat-error *ngIf="teacherForm.get('name')?.errors?.required">{{'fieldErrors.nameIsRequired'|translate}}</mat-error>
      <mat-error *ngIf="teacherForm.get('name')?.errors?.minlength">{{'fieldErrors.minLengthTeacher'|translate}}</mat-error>
      <mat-error *ngIf="teacherForm.get('name')?.errors?.pattern">{{'fieldErrors.namePattern'|translate}}</mat-error>
    </mat-form-field>
    <mat-slide-toggle 
    formControlName="isActive"
    [attr.data-cy-input]="'isActive:' + teacherForm.get('isActive')?.value">{{'labels.active'|translate}}</mat-slide-toggle>
    <div *ngIf="isLoading;then showLoader;else showButton"></div>
    <ng-template #showLoader>
      <mat-spinner diameter="50"></mat-spinner>
    </ng-template>
    <ng-template #showButton>
      <button  
      mat-raised-button [disabled]="teacherForm.invalid" 
      data-cy-teacher-button="save" 
      [attr.data-cy-is-disabled]="teacherForm.invalid" 
      type="submit">{{(isEdit ? 'labels.save':'labels.create')|translate}}</button>
    </ng-template>
  </form>
</div>

import { Component, OnInit } from '@angular/core';
import {FormBuilder, FormControl, Validators} from "@angular/forms";
import {PageEvent} from "@angular/material/paginator";
import {TeacherService} from "../../services/teacher.service";
import { of } from 'rxjs';
import Teacher from 'src/app/shared/model/teacher';

@Component({
  selector: 'app-teacher-page',
  templateUrl: './teacher-page.component.html',
  styleUrls: ['./teacher-page.component.scss']
})
export class TeacherPageComponent implements OnInit {

  isLoading = true;
  isChangingPage = false;

  teachers: any[] = [];

  totalLength = 0;
  pageSize = 5;

  teacherForm = this.fb.group({
    email: ['',Validators.email],
    name: ['',Validators.required],
    school: ['', Validators.required],
    isActive:['']
  });

  searchControl = new FormControl('');

  filteredTeachers$ = of([] as Teacher[]);

  teacherToggleId: number | null = null;

  createMode = true;

  constructor(private teacherService: TeacherService, private fb: FormBuilder) {
    this.refresh();
  }

  ngOnInit(): void {

    this.teacherForm.get('name')?.valueChanges.subscribe(value => {
      if(!value?.id){
        // this.filteredSchools$ = this.schoolService.findSchoolByNameSimilarity(value);
        if (value === ""){
          this.refresh();
        } else {
          this.teacherService.findTeacherByNameSimilarity(value).subscribe (teachers => {
            this.teachers = teachers;
          }
        );
        }
      }
    })

  }

  refresh(){
    this.teacherService.getTeachersUsers(this.pageSize,0).subscribe(response => {
      this.teachers = response.content;
      this.totalLength = response.totalElements;
      this.isLoading = false;
    })
  }

  pageChange(pageEvent: PageEvent) {
    this.isChangingPage = true;
    this.teacherService.getTeachersUsers(pageEvent.pageSize,pageEvent.pageIndex).subscribe(response => {
      this.teachers = response.content;
      this.pageSize = pageEvent.pageSize;
      this.totalLength = response.totalElements;
      this.isChangingPage = false;
    })
  }

  displayTeacherName(teacher: Teacher){
    return teacher && teacher.name ? teacher.name: '';
  }


}
