import {Component, EventEmitter, Input, OnInit, Output, SimpleChanges} from '@angular/core';
import {FormBuilder, Validators} from "@angular/forms";
import {of} from "rxjs";
import School from "../../../../shared/model/school";
import {ActivatedRoute, Router} from "@angular/router";
import {ManagerService} from "../../../../admin/manager/services/manager.service";
import {SchoolService} from "../../../../admin/schools/services/school.service";
import {MatSnackBar} from "@angular/material/snack-bar";
import {TranslateService} from "@ngx-translate/core";
import {MatTableDataSource} from "@angular/material/table";
import {TeacherService} from "../../../teacher/services/teacher.service";
import {concatMap, debounceTime, exhaustMap, filter, switchMap} from "rxjs/operators";
import {ClassService} from "../../services/class.service";

@Component({
  selector: 'app-class-form',
  templateUrl: './class-form.component.html',
  styleUrls: ['./class-form.component.scss']
})
export class ClassFormComponent implements OnInit {

  isEdit = false;

  @Input() classId:number | null = null;
  @Input() createMode = true;

  @Output() save = new EventEmitter();

  clear = false;

  teachers:any[] = [];
  students:any[] = [];
  classNameControl = this.fb.control('',Validators.required);
  isActiveControl = this.fb.control('',Validators.required);

  isLoading = false;


  constructor(
    private route: ActivatedRoute,
    private fb: FormBuilder,
    private classService: ClassService,
    private translateService: TranslateService,
    private snackbar: MatSnackBar
  ) { }

  ngOnInit(): void {
    this.route.params.subscribe(value => {
      if(value['id']){
        this.isEdit = true;
        this.classId = value['id'];

      }
    })
  }

  ngOnChanges(changes: SimpleChanges){
    this.isEdit = !this.createMode;
    if(this.createMode){
      this.teachers = [];
      this.students = [];
      this.classNameControl.reset();
      this.clear = true;
      setTimeout(() => {
        this.clear = false;
      },500)
    }
    else{
      this.classService.getClassById(this.classId).subscribe(response => {
        this.teachers = response.teachers.map((t:any) => ({isActive: t.isActive,name: t.teacher.name,email: t.teacher.email,id:t.teacher.id}));
        this.students = response.students.map((s:any) => ({isActive: s.isActive,name: s.student.name,registry: s.student.registry,id:s.student.id}));
        this.classNameControl.setValue(response.name);
        this.isActiveControl.setValue(response.isActive);
      })
    }
  }

  createClass(){
    this.isLoading = true;
    const teachersId = this.teachers.map(teacher => teacher.id);
    const studentsId = this.students.map(student => student.id);
    this.classService.createClass({
      teachersId,
      studentsId,
      className: this.classNameControl.value,
      isActive: this.isActiveControl.value
    }).subscribe( success => {
      this.translateService.get('messages.classCreated').subscribe( translation => {
        this.isLoading = false;
        this.save.emit();
        this.snackbar.open(translation,'Fechar').afterDismissed();
        this.isLoading = false;
      })
    }, error => {
      this.isLoading = false;
    })
  }

  updateStatusAndName(){
    this.isLoading = true;
    this.classService.updateClassNameAndStatus({name: this.classNameControl.value,isActive: this.isActiveControl.value},this.classId).subscribe(success => {
      this.translateService.get('messages.classNameAndStatusUpdated').subscribe( translation => {
        this.isLoading = false;
        this.save.emit();
        this.snackbar.open(translation,'Fechar').afterDismissed();
      })
    }, error => {
      this.isLoading = false;
    })
  }

  displayName(teacher: any){
    return teacher && teacher.name ? teacher.name: '';
  }

  get teachersCompleted(){
    return this.teachers.length > 0;
  }

  get studentsCompleted(){
    return this.students.length > 0;
  }
}
<div class="default-padding full-width" fxLayout="column" fxLayoutAlign="center start">

  <h1 class="title">
    {{(isEdit?'labels.editClass' : 'labels.createClass') |translate}}
  </h1>
  <mat-stepper class="full-width" orientation="vertical" [linear]="true" #stepper fxFlex="100">
    <mat-step [completed]="teachersCompleted">
      <ng-template matStepLabel>{{'labels.chooseTeachers'|translate}}</ng-template>
      <app-teacher-table
        (next)="stepper.next()"
        [(teachers)]="teachers"
        [clear]="clear"
        [classId]="classId"
        [editMode]="isEdit">
      </app-teacher-table>
    </mat-step>
    <mat-step>
      <ng-template matStepLabel>{{'labels.chooseStudents'|translate}}</ng-template>
      <app-student-table
        (previous)="stepper.previous()"
        (next)="stepper.next()"
        [(students)]="students"
        [clear]="clear"
        [classId]="classId">
      </app-student-table>
    </mat-step>
    <mat-step [completed]="studentsCompleted">
      <ng-template matStepLabel>{{'labels.chooseClassName'|translate}}</ng-template>
      <div fxLayout="row" fxFlex="100" fxLayoutAlign="space-between">
        <mat-form-field
          fxFlex="80" appearance="outline">
          <mat-label>{{'labels.className'|translate}}</mat-label>
          <input
            data-cy-input="name"
            matInput
            type="text"
            [formControl]="classNameControl"
            placeholder="{{'placeholder.className'|translate}}"
            maxlength="20"
            pattern="[A-Z a-z 0-9]+"
            required>
          <mat-error *ngIf="classNameControl.hasError('required')">{{'fieldErrors.classNameIsRequired'|translate}}</mat-error>
          <mat-error *ngIf="classNameControl.hasError('pattern')">{{'fieldErrors.classNamePattern'|translate}}</mat-error>
        </mat-form-field>
        <mat-slide-toggle 
        [formControl]="isActiveControl"
        [attr.data-cy-input]="'isActive:' + classNameControl.get('isActive')?.value" >{{'labels.active'|translate}}</mat-slide-toggle>
      </div>
      <button
        mat-flat-button
        color="primary"
        [disabled]="!teachersCompleted || !studentsCompleted || classNameControl.invalid"
        data-cy-class-button="save" 
        [attr.data-cy-is-disabled]="classNameControl.invalid" 
        (click)="isEdit? updateStatusAndName():createClass()">{{(isEdit?'labels.save':'labels.createClass')|translate}}</button>
    </mat-step>
  </mat-stepper>
</div>

