import {ActivityService} from "../../services/activity.service";
import * as moment from "moment";
import {ClassService} from "../../../../manager/school-classes/services/class.service";
import {SnackbarService} from "../../../../shared/services/snackbar.service";

@Component({
  selector: 'app-activities-form',
  templateUrl: './activities-form.component.html',
  styleUrls: ['./activities-form.component.scss']
})
export class ActivitiesFormComponent implements OnInit {

  isEdit = false;

  @Input() activityId:number | null = null;
  @Input() createMode = true;

  @Output() save = new EventEmitter();

  alreadyRegisteredEmail = false;

  file:any = null;

  activityForm = this.fb.group({
    name: ['',Validators.required],
    description: ['',Validators.required],
    schoolClasses: ['',Validators.required],
    punctuation: ['',Validators.required],
    isVisible: [false,Validators.required],
    isActive: [false,Validators.required],
    maxDeliveryDate:['',Validators.required],
    filename: [{value: ''}]
  });

  filteredClasses = [] as any[];
  activities = [] as any[];

  isLoading = false;

  displayClassName = (schoolClass:any) => schoolClass?.name || '' ;

  constructor(
    private route: ActivatedRoute,
    private fb: FormBuilder,
    private activityService: ActivityService,
    private schoolService: SchoolService,
    private snackbarService: SnackbarService,
    private translateService: TranslateService,
    private classService: ClassService
  ) { }

  ngOnInit(): void {
    this.classService.getTeacherHimselfClasses().subscribe(classes => {
      this.filteredClasses = classes;
    });
  }

  ngOnChanges(changes: SimpleChanges){
    this.isEdit = !this.createMode;
    if(this.createMode){
      this.activityForm.reset();
    }
    else{
      this.activityService.getTeacherActivityById(this.activityId as number).subscribe(({id,maxDeliveryDate,classes,...rest}) =>{
        this.activityForm.setValue({
          maxDeliveryDate: moment(maxDeliveryDate),
          schoolClasses: classes.map((c: any) => c.id),
          ...rest
        });
      });
    }
  }

  uploadFile($event:any){
    this.file = $event.target.files[0];
    console.log($event?.target);
    this.activityForm.get('filename')?.setValue(this.file.name);
  }

  updateActivity(){
    this.isLoading = true;
    const {maxDeliveryDate,filename,...rest} = this.activityForm.value;
    const formDataRequestBody = new FormData();
    const jsonRequestBody = {
      maxDeliveryDate: maxDeliveryDate.format('YYYY-MM-DD kk:mm:ss'),
      document: this.file,
      ...rest
    };
    for(let key in jsonRequestBody){
      formDataRequestBody.append(key,jsonRequestBody[key]);
    }
    this.activityService.updateActivity(
      this.activityId,
      formDataRequestBody).subscribe(success => {
      this.snackbarService.showSnack('messages.activityUpdated','labels.close');
      this.isLoading = false;
      this.save.emit();
    }, error => {
      this.isLoading = false;
    });
  }

  createActivity(){
    this.isLoading = true;
    const {maxDeliveryDate,filename,...rest} = this.activityForm.value;
    const formDataRequestBody = new FormData();
    const jsonRequestBody = {
      maxDeliveryDate: maxDeliveryDate.format('YYYY-MM-DD kk:mm:ss'),
      document: this.file,
      ...rest
    };
    for(let key in jsonRequestBody){
      formDataRequestBody.append(key,jsonRequestBody[key]);
    }
    this.activityService.createActivity(formDataRequestBody).subscribe(success => {
      this.snackbarService.showSnack('messages.activityCreated','labels.close');
      this.save.emit();
      this.activityForm.reset();
    }, error => {
      this.isLoading = false;
    });
  }

  downloadAttachment(){
    this.activityService.downloadTeacherAttachment(this.activityId as number).subscribe(response => {
      console.log(response.headers)
      const a = document.createElement('a')
      const objectUrl = URL.createObjectURL(response.body)
      a.href = objectUrl
      a.download = response.headers.get('filename') || '';
      a.click();
      URL.revokeObjectURL(objectUrl);
    });
  }

  isExpired(){
    const expireDate = this.activityForm.get('maxDeliveryDate')?.value;
    return moment().isAfter(expireDate);
  }

}
<div class="default-padding full-width" fxLayout="column" fxLayoutAlign="center start">

  <h1 class="title">
    {{(isEdit?'labels.updateActivity' : 'labels.createActivity') |translate}}
  </h1>

  <form
    fxLayout="row wrap"
    fxLayoutAlign="space-between"
    class="full-width"
    fxFlex="100"
    [formGroup]="activityForm">
    <mat-form-field
      fxFlex="60" appearance="outline">
      <mat-label>{{'labels.name'|translate}}</mat-label>
      <input
        data-cy-input="name"
        matInput
        type="text"
        formControlName="name"
        pattern="^[a-z A-Z 0-9]*$"
        required>
      <mat-error *ngIf="activityForm.get('name')?.errors?.required">{{'fieldErrors.activityNameIsRequired'|translate}}</mat-error>
      <mat-error *ngIf="activityForm.get('name')?.errors?.pattern">{{'fieldErrors.activityNamePattern'|translate}}</mat-error>
    </mat-form-field>
    <mat-form-field
      fxFlex="30" appearance="outline">
      <mat-label>{{'labels.punctuation'|translate}}</mat-label>
      <input
        matInput
        type="text"
        data-cy-input="punctuation"
        formControlName="punctuation"
        pattern="^[\d,.?!]+$"
        required>
      <mat-error *ngIf="activityForm.get('punctuation')?.errors?.required">{{'fieldErrors.punctuationIsRequired'|translate}}</mat-error>
      <mat-error *ngIf="activityForm.get('punctuation')?.errors?.pattern">{{'fieldErrors.punctuationPattern'|translate}}</mat-error>
    </mat-form-field>
    <mat-form-field fxFlex="90" appearance="outline">
      <mat-label>{{'labels.classes'|translate}}</mat-label>
      <mat-select data-cy-select="class" multiple formControlName="schoolClasses" required>
        <mat-option [attr.data-cy-select]="schoolClass.id" *ngFor="let schoolClass of filteredClasses"  [value]="schoolClass.id">
          {{schoolClass.name}}
        </mat-option>
      </mat-select>
      <mat-error *ngIf="activityForm.get('schoolClasses')?.errors?.required">{{'fieldErrors.schoolClassesIsRequired'|translate}}</mat-error>
    </mat-form-field>
    <mat-form-field fxFlex="90" appearance="outline">
      <input
        matInput
        data-cy-input="maxDeliveryDate"
        [ngxMatDatetimePicker]="picker"
        placeholder="{{'placeholder.chooseDeliveryDate'|translate}}"
        formControlName="maxDeliveryDate"
        required>
      <mat-datepicker-toggle matSuffix [for]="$any(picker)"></mat-datepicker-toggle>
      <ngx-mat-datetime-picker
        #picker>
      </ngx-mat-datetime-picker>
      <mat-error *ngIf="activityForm.get('maxDeliveryDate')?.errors?.required">{{'fieldErrors.maxDeliveryDateIsRequired'|translate}}</mat-error>
    </mat-form-field>
    <mat-form-field
      fxFlex="90" appearance="outline">
      <mat-label>{{'labels.attachment'|translate}}</mat-label>
      <input
        data-cy-input="fileName"
        matInput
        type="text"
        [readonly]="true"
        formControlName="filename">
      <button
        matSuffix
        mat-icon-button
        (click)="uploader.click()">
        <mat-icon>attach_file</mat-icon>
      </button>
      <button
        *ngIf="isEdit === true && activityForm.get('filename')?.value !== ''"
        matSuffix
        mat-icon-button
        (click)="downloadAttachment()">
        <mat-icon>file_download</mat-icon>
      </button>
    </mat-form-field>
    <input
      hidden
      type="file"
      #uploader
      (change)="uploadFile($event)">
    <mat-form-field
      fxFlex="45" appearance="outline">
      <mat-label>{{'labels.description'|translate}}</mat-label>
      <textarea matInput data-cy-input="description" formControlName="description" required></textarea>
      <mat-error *ngIf="activityForm.get('description')?.errors?.required">{{'fieldErrors.descriptionIsRequired'|translate}}</mat-error>
    </mat-form-field>
    <mat-checkbox data-cy-input="isVisible" [indeterminate]="false" formControlName="isActive">{{'labels.active'|translate}}</mat-checkbox>
    <mat-checkbox data-cy-input="isActive" [indeterminate]="false" formControlName="isVisible">{{'labels.visible'|translate}}</mat-checkbox>
    <mat-checkbox data-cy-input="isDeliverable" [indeterminate]="false" formControlName="isDeliverable">{{'labels.deliverable'|translate}}</mat-checkbox>
    <div *ngIf="isLoading;then showLoader;else showButton"></div>
    <ng-template #showLoader>
      <mat-spinner diameter="50"></mat-spinner>
    </ng-template>
    <ng-template #showButton>
      <button mat-raised-button
      data-cy-activity-button="save" 
      [attr.data-cy-is-disabled]="activityForm.invalid"  
       (click)="isEdit ? updateActivity() : createActivity()" [disabled]="activityForm.invalid">{{(isEdit ? 'labels.save':'labels.create')|translate}}</button>
    </ng-template>
  </form>
  <button *ngIf="isExpired()" mat-flat-button [routerLink]="[activityId,'grades']" color="primary">
    {{'labels.viewDeliveries'|translate}}
  </button>
</div>

import { Component, OnInit } from '@angular/core';
import {FormControl} from "@angular/forms";
import {StudentsService} from "../../../../manager/students/services/students.service";
import {PageEvent} from "@angular/material/paginator";
import {ActivityService} from "../../services/activity.service";

@Component({
  selector: 'app-activities-page',
  templateUrl: './teacher-activities-page.component.html',
  styleUrls: ['./teacher-activities-page.component.scss']
})
export class TeacherActivitiesPage implements OnInit {

  isLoading = false;
  isChangingPage = false;

  activities:any[] = [];

  totalLength = 0;
  pageSize = 5;

  searchControl = new FormControl('');

  activityToggleId: number | null = null;

  createMode = true;

  classes = ['MAT-5A','MAT-6A','MAT-7A','MAT-8A','MAT-9A'];
  colors = ['#FFC600','#BCED09','#8338EC','#FCF300','#FF715B','#B5179E','#FB5607','#B5179E']

  constructor(private activityService: ActivityService) {
    this.refresh();
  }

  ngOnInit(): void {
  }

  refresh(){
    this.activityService.getTeacherActivitiesPaginated(this.pageSize,0).subscribe(response => {
      this.activities = response.content;
      this.totalLength = response.totalElements;
      this.isLoading = false;
    })
  }

  pageChange(pageEvent: PageEvent) {
    this.isChangingPage = true;
    this.activityService.getTeacherActivitiesPaginated(pageEvent.pageSize,pageEvent.pageIndex).subscribe(response => {
      this.activities = response.content;
      this.pageSize = pageEvent.pageSize;
      this.totalLength = response.totalElements;
      this.isChangingPage = false;
    })
  }
}
