import { Injectable } from '@angular/core';
import {
  ActivatedRoute,
  ActivatedRouteSnapshot,
  CanActivate,
  Router,
  RouterStateSnapshot,
  UrlTree
} from '@angular/router';
import {iif, Observable, of} from 'rxjs';
import {environment} from "../../../environments/environment";
import {concatMap, take} from "rxjs/operators";
import {AngularFireAuth} from "@angular/fire/auth";
import {HttpClient} from "@angular/common/http";

@Injectable({
  providedIn: 'root'
})
export class HomeGuard implements CanActivate {

  constructor(
    private router: Router,
    private firebaseAuth: AngularFireAuth,
    private activatedRoute: ActivatedRoute,
    private http: HttpClient
  ) {
  }

  canActivate(
    route: ActivatedRouteSnapshot,
    state: RouterStateSnapshot): Observable<boolean | UrlTree> | Promise<boolean | UrlTree> | boolean | UrlTree {
    return this.firebaseAuth.user.pipe(
      concatMap(user => {
        if(user!=null){
          return this
                  .getRole()
                  .pipe(
                    concatMap(role => of(this.redirectAccordingToRole(role)))
                  );
        }else{
          return of(this.router.createUrlTree(['/login']));
        }
      })
    )
  }


  private redirectAccordingToRole(role: string){
    switch (role){
      case 'ADMIN':
        return this.router.createUrlTree(['/admin/dashboard']);
      case 'STUDENT':
        return this.router.createUrlTree(['/student/dashboard']);
      case 'TEACHER':
        return this.router.createUrlTree(['/teacher/dashboard']);
      case 'MANAGER':
        return this.router.createUrlTree(['/manager/dashboard']);
      default:
        return this.router.createUrlTree(['/login']);
    }
  }

  getRole(){
    return this.http.get<string>(`${environment.apiUrl}/api/roles`,{responseType: 'text' as 'json'})
      .pipe(take(1));
  }

}

import { Injectable } from '@angular/core';
import { ActivatedRouteSnapshot, CanActivate, RouterStateSnapshot, UrlTree } from '@angular/router';
import { Observable } from 'rxjs';
import {AuthenticationService} from "../services/authentication.service";
import {map} from "rxjs/operators";

@Injectable({
  providedIn: 'root'
})
export class IsStudentGuard implements CanActivate {
  constructor(private auth: AuthenticationService) {}

  canActivate(
    route: ActivatedRouteSnapshot,
    state: RouterStateSnapshot): Observable<boolean | UrlTree> | Promise<boolean | UrlTree> | boolean | UrlTree {
    return this.auth
      .getRole()
      .pipe(
        map(role => role === 'STUDENT')
      );
  }

}

import { Injectable } from '@angular/core';
import {HttpClient} from "@angular/common/http";
import {environment} from "../../../../environments/environment";
import {take} from "rxjs/operators";

@Injectable({
  providedIn: 'root'
})
export class StudentDeliveryService {

  constructor(private http: HttpClient) { }

  deliveryResolution(activityId: number, formDataRequestBody: FormData) {
    return this.http.post<any>(`${environment.apiUrl}/api/activity-deliveries/activity/${activityId}`,formDataRequestBody).pipe(take(1));
  }

  downloadDeliveryAttachment(activityId: number) {
    return this.http.get<any>(
      `${environment.apiUrl}/api/activity-deliveries/student/activity/${activityId}/download`,
      {responseType: 'blob' as 'json',observe: 'response'}
    ).pipe(take(1));
  }
}

import { Injectable } from '@angular/core';
import {TranslateService} from "@ngx-translate/core";

declare let countdown: any;
declare let moment: any;

@Injectable({
  providedIn: 'root'
})
export class CountdownService {

  constructor(private translateService: TranslateService) {
    let currentLang = this.translateService.currentLang;
    if(currentLang === 'pt'){
      countdown.setLabels(
        ' milissegundo| segundo| minuto| hora| dia| semana| ms| ano| dcada| sculo| milnio',
        ' milissegundos| segundos| minutos| horas| dias| semanas| meses| anos| dcadas| sculos| milnios',
        ' e ',
        ' , ',
        'agora');
    }

    this.translateService.onLangChange.subscribe(({lang}) => {
      if(lang === 'pt'){
        countdown.setLabels(
          ' milissegundo| segundo| minuto| hora| dia| semana| ms| ano| dcada| sculo| milnio',
          ' milissegundos| segundos| minutos| horas| dias| semanas| meses| anos| dcadas| sculos| milnios',
          ' e ',
          ' + ',
          'agora');
      }else {
        countdown.resetLabels();
      }
    })
  }

  countdownTo(date: string){
    return moment().countdown(date,~(countdown.WEEKS | countdown.MILLISECONDS | countdown.SECONDS)).toString();
  }
}

import { Pipe, PipeTransform } from '@angular/core';
import {CountdownService} from "../services/countdown.service";

@Pipe({
  name: 'countdownTo'
})
export class CountdownToPipe implements PipeTransform {

  constructor(private countdownService: CountdownService) {
  }

  transform(value: string, ...args: unknown[]): unknown {
    return this.countdownService.countdownTo(value);
  }

}

import {Component, EventEmitter, Input, OnInit, Output, SimpleChanges} from '@angular/core';
import {FormBuilder, FormControl, Validators} from "@angular/forms";
import {ActivatedRoute} from "@angular/router";
import {ActivityService} from "../../../../teacher/activities/services/activity.service";
import {SchoolService} from "../../../../admin/schools/services/school.service";
import {MatSnackBar} from "@angular/material/snack-bar";
import {TranslateService} from "@ngx-translate/core";
import {ClassService} from "../../../../manager/school-classes/services/class.service";
import * as moment from "moment";
import {StudentDeliveryService} from "../../services/student-delivery.service";

@Component({
  selector: 'app-student-delivery',
  templateUrl: './student-delivery.component.html',
  styleUrls: ['./student-delivery.component.scss']
})
export class StudentDeliveryComponent implements OnInit {

  isEdit = false;

  @Input() activityId:[number,number] = [0,0];
  @Input() createMode = true;

  @Output() save = new EventEmitter();

  isDelivered = false;

  filenameControl = new FormControl('');

  activity:any;

  file:any = null;

  filteredClasses = [] as any[];

  isLoading = false;

  displayClassName = (schoolClass:any) => schoolClass?.name || '' ;

  constructor(
    private route: ActivatedRoute,
    private fb: FormBuilder,
    private activityService: ActivityService,
    private schoolService: SchoolService,
    private snackbar: MatSnackBar,
    private translateService: TranslateService,
    private classService: ClassService,
    private studentDeliveryService: StudentDeliveryService
  ) { }

  ngOnInit(): void {
    this.classService.getTeacherHimselfClasses().subscribe(classes => {
      this.filteredClasses = classes;
    });
  }

  ngOnChanges(changes: SimpleChanges){
    this.isEdit = !this.createMode;
    if(this.createMode){
    }
    else{
      this.activityService.getStudentActivityById(this.activityId[0],this.activityId[1]).subscribe(({filename,deliveryFilename,...rest}) =>{
        this.activity = {filename,deliveryFilename,...rest};
        this.filenameControl.setValue(deliveryFilename);
        this.isDelivered = !!deliveryFilename;
      });
    }
  }

  uploadFile($event:any){
    this.file = $event.target.files[0];
    this.filenameControl.setValue(this.file?.name);
  }

  deliveryActivity(){
    this.isLoading = true;
    const formDataRequestBody = new FormData();
    formDataRequestBody.append("document",this.file);
    this.studentDeliveryService.deliveryResolution(this.activityId[0],formDataRequestBody).subscribe(success => {
      this.translateService.get('messages.studentRegistered').subscribe( translation => {
        this.isLoading = false;
        this.save.emit();
        this.isDelivered = true;
        this.snackbar.open(translation,'Fechar');
      })
    }, error => {
      this.isLoading = false;
    });
  }

  downloadDelivery() {
    this.studentDeliveryService.downloadDeliveryAttachment(this.activityId[0] as number).subscribe(response => {
      const a = document.createElement('a')
      const objectUrl = URL.createObjectURL(response.body)
      a.href = objectUrl
      a.download = response.headers.get('filename') || '';
      a.click();
      URL.revokeObjectURL(objectUrl);
    });
  }

  downloadAttachment(){
    this.activityService.downloadStudentAttachment(this.activityId[0] as number).subscribe(response => {
      const a = document.createElement('a')
      const objectUrl = URL.createObjectURL(response.body)
      a.href = objectUrl
      a.download = response.headers.get('filename') || '';
      a.click();
      URL.revokeObjectURL(objectUrl);
    });
  }

  showSnackbar(message: string){
    this.snackbar.open(message,'Fechar');
  }
}
<div>
  <div fxLayout="row" fxLayoutAlign="space-between center">
    <p class="activity-name">{{activity?.name}}</p>
    <p class="expires" *ngIf="!isDelivered">{{'labels.expiresIn'|translate}} {{activity?.expireDate|countdownTo}}</p>
    <p class="expires" *ngIf="isDelivered">{{'labels.scoreReceived'|translate}} {{activity?.scoreReceived| number : '1.2-2'}}</p>
  </div>
  <p class="school-class-name">{{'labels.school-classes'|translate}}: {{activity?.schoolClassName}}</p>
  <p class="description-title">{{'labels.description'|translate}}</p>
  <p class="description">
    {{activity?.description}}
  </p>
  <p class="attachment-title" *ngIf="activity?.filename">{{'labels.attachment'|translate}}</p>
  <div fxLayout="row" fxLayoutAlign="start center" *ngIf="activity?.filename">
    <p>{{activity?.filename}}</p>
    <button
      mat-icon-button
      (click)="downloadAttachment()">
      <mat-icon>file_download</mat-icon>
    </button>
  </div>
  <p class="delivery-title">
    <mat-form-field
      fxFlex="90" appearance="outline">
      <mat-label>{{'labels.delivery'|translate}}</mat-label>
      <input
        matInput
        type="text"
        [readonly]="true"
        [formControl]="filenameControl">
      <button
        *ngIf="!isDelivered"
        matSuffix
        mat-icon-button
        (click)="uploader.click()">
        <mat-icon>attach_file</mat-icon>
      </button>
      <button
        matSuffix
        mat-icon-button
        *ngIf="isDelivered"
        (click)="downloadDelivery()">
        <mat-icon>file_download</mat-icon>
      </button>
    </mat-form-field>
    <input
      hidden
      type="file"
      #uploader
      (change)="uploadFile($event)">
  </p>
  <div class="full-width" fxLayout="row" fxLayoutAlign="end center">
    <button mat-flat-button color="primary" *ngIf="!isDelivered" (click)="deliveryActivity()" [disabled]="filenameControl?.value === ''">
      {{'labels.send'|translate}}
    </button>
  </div>
</div>

import { Component, OnInit } from '@angular/core';
import {FormControl} from "@angular/forms";
import {ActivityService} from "../../../../teacher/activities/services/activity.service";
import {PageEvent} from "@angular/material/paginator";
import {CountdownService} from "../../../../shared/services/countdown.service";
import * as moment from "moment";

@Component({
  selector: 'app-student-activities',
  templateUrl: './student-activities-page.component.html',
  styleUrls: ['./student-activities-page.component.scss']
})
export class StudentActivitiesPageComponent implements OnInit {

  isLoading = false;
  isChangingPage = false;

  activities:any[] = [];

  totalLength = 0;
  pageSize = 5;

  searchControl = new FormControl('');

  activityToggleId: [number,number] = [0,0];

  createMode = true;

  classes = ['MAT-5A','MAT-6A','MAT-7A','MAT-8A','MAT-9A'];
  colors = ['#FFC600','#BCED09','#8338EC','#FCF300','#FF715B','#B5179E','#FB5607','#B5179E']

  constructor(
    private activityService: ActivityService,) {
    this.refresh();
  }

  ngOnInit(): void {}

  refresh(){
    this.activityService.getStudentActivities().subscribe(response => {
      this.activities = response;
      this.isLoading = false;
    })
  }

  activityStatus(activity: any){
    const deliveryDate = moment(activity.expireDate);
    const now = moment();
    let diff = deliveryDate.diff(now,'minutes');
    if(diff < 1){
      return 'status-expired';
    } else if (activity.isRevised){
      return 'status-revised';
    } else if(activity.isDelivered){
      return 'status-delivered';
    } else{
      return 'status-to-delivery';
    }
  }

  isExpired(activity: any){
    const deliveryDate = moment(activity.expireDate);
    const now = moment();
    let diff = deliveryDate.diff(now,'minutes');
    return diff < 1;
  }
}
