            mat-raised-button
            color="primary"
            class="button-margin"
            id="button-login"
            (click)="login()">
            {{ 'labels.login' | translate }}
          </button>
        </ng-template>
        <button
          mat-stroked-button
          class="button-margin"
          routerLink="/reset-password"
          color="primary">
          {{'labels.forgotPassword'|translate}}
        </button>
      </div>
    </div>
