import {TeacherService} from "../../../teacher/services/teacher.service";
import {MatTableDataSource} from "@angular/material/table";
import {catchError, concatMap, debounceTime, filter, startWith, tap} from "rxjs/operators";
import {FormBuilder} from "@angular/forms";
import {ClassService} from "../../services/class.service";
import {MatSnackBar} from "@angular/material/snack-bar";
import {TranslateService} from "@ngx-translate/core";
import {of} from "rxjs";

@Component({
  selector: 'app-teacher-table',
  templateUrl: './teacher-table.component.html',
  styleUrls: ['./teacher-table.component.scss']
})
export class TeacherTableComponent implements OnInit {

  @Output() next = new EventEmitter();
  @Output() teachersChange = new EventEmitter<any>();

  @Input() clear = false;
  @Input() teachers:any[] = [];
  @Input() classId: any;
  @Input() editMode = false;

  teacherColumns = ['name','email','actions']
  teacherDataSource = new MatTableDataSource<any>([]);
  teacherEmail = this.fb.control('');

  teachersOptions: any[] = [];

  constructor(
    private teacherService: TeacherService,
    private classService: ClassService,
    private fb: FormBuilder,
    private snackbar: MatSnackBar,
    private translateService: TranslateService
  ) { }

  ngOnInit(): void {
    this.teacherEmail
      .valueChanges
      .pipe(
        tap(console.log),
        startWith(''),
        filter(e => e != null && e?.length > 3),
        debounceTime(1000),
        concatMap(e => {
          return this.teacherService.findByEmailSimilarity(e);
        }),
        catchError(e => {
          console.log(e);
          return of([]);
        })
      ).subscribe((teachers: any) => {
      this.teachersOptions = teachers;
    })
  }

  ngOnChanges(changes: SimpleChanges){
    if(this.clear){
      this.teacherEmail.reset();
      this.teacherDataSource.data = this.teachers;
    }else{
      this.teacherEmail.reset();
      this.teacherDataSource.data = this.teachers;

    }
  }

  addTeacher(){
    this.teachersChange.emit([...this.teachers,this.teacherEmail.value]);
    if(this.editMode){
      this.classService.addTeacherToClass(this.teacherEmail.value.id,this.classId).subscribe(success =>{
        this.translateService.get('messages.teacherStatusChangeSuccess').subscribe( translation => {
          this.snackbar.open(translation,'Fechar').afterDismissed();
          this.teacherEmail.reset();
        })
      });
    }
  }

  removeTeacher(teacherId: any){
    this.teachersChange.emit([...this.teachers.filter(t => t.id != teacherId)]);
  }

  changeStatus(status: boolean,teacherId: any){
    this.classService.updateTeacherStatus(status,this.classId,teacherId).subscribe(success => {
      this.translateService.get('messages.teacherStatusChangeSuccess').subscribe( translation => {
        this.snackbar.open(translation,'Fechar').afterDismissed();
      })
    },error => {
      this.translateService.get('error.teacherStatusChangeError').subscribe( translation => {
        this.snackbar.open(translation,'Atualizar pgina').afterDismissed().subscribe(() =>{

        });
      })
    });
  }

  displayName(teacher: any){
    return teacher && teacher.name ? teacher.name: '';
  }

  isInvalid() {
    return this.teachers.length < 1;
  }

  emitNext(){
    this.next.emit();
  }
}
<div fxLayout="row" class="full-width" fxLayoutAlign="space-between">
  <mat-form-field appearance="outline" fxFlex="80">
    <input
      matInput
      type="text"
      [formControl]="teacherEmail"
      placeholder="{{'placeholder.teacherEmail'|translate}}"
      required
      [matAutocomplete]="auto">
  </mat-form-field>
  <mat-autocomplete #auto="matAutocomplete" [displayWith]="displayName">
    <mat-option *ngFor="let teacher of teachersOptions"  [value]="teacher">
      {{teacher.name}}
    </mat-option>
  </mat-autocomplete>
  <button mat-mini-fab color="primary" (click)="addTeacher()">
    <mat-icon>add</mat-icon>
  </button>
</div>
<table mat-table [dataSource]="teacherDataSource" class="mat-elevation-z1 full-width">

  <ng-container matColumnDef="name">
    <th mat-header-cell *matHeaderCellDef> {{'labels.name'|translate}} </th>
    <td mat-cell *matCellDef="let element"> {{element.name}} </td>
  </ng-container>

  <ng-container matColumnDef="email">
    <th mat-header-cell *matHeaderCellDef> E-mail </th>
    <td mat-cell *matCellDef="let element"> {{element.email}} </td>
  </ng-container>

  <ng-container matColumnDef="actions">
    <th mat-header-cell *matHeaderCellDef> {{'labels.actions'|translate}} </th>
    <td mat-cell *matCellDef="let element">
      <mat-slide-toggle
        #toggle
        [(ngModel)]="element.isActive"
        (click)="changeStatus(!toggle.checked,element.id)">
        {{(toggle.checked?'labels.active':'labels.inactive')|translate}}
      </mat-slide-toggle>
      <button *ngIf="!classId" mat-icon-button color="warn" (click)="removeTeacher(element.id)">
        <mat-icon>delete</mat-icon>
      </button>
    </td>
  </ng-container>

  <tr mat-header-row *matHeaderRowDef="teacherColumns"></tr>
  <tr mat-row *matRowDef="let row; columns: teacherColumns;"></tr>
</table>
<div fxLayout="row" class="full-width default-padding" fxLayoutAlign="end center">
  <button mat-flat-button [disabled]="isInvalid()" (click)="emitNext()" color="primary">{{'labels.next'|translate}}</button>
</div>

import { Injectable } from '@angular/core';
import {HttpClient} from "@angular/common/http";
import {TranslateService} from "@ngx-translate/core";
import {MatSnackBar} from "@angular/material/snack-bar";
import {Router} from "@angular/router";
import {environment} from "../../../../environments/environment";
import {take} from "rxjs/operators";

@Injectable({
  providedIn: 'root'
})
export class StudentsService {

  isLoading = false;

  constructor(
    private http: HttpClient,
    private translateService: TranslateService,
    private snackbar: MatSnackBar,
    private router: Router
  ) { }

  registerStudent(value: any) {
    const {school,...rest} = value;
    const language = this.translateService.currentLang;
    return this.http.post<any>(`${environment.apiUrl}/api/students`, {language,...rest});
  }

  getStudents(pageSize: number, pageNumber: number) {
    return this.http.get<any>(`${environment.apiUrl}/api/students?size=${pageSize}&pageNumber=${pageNumber}`).pipe(take(1));
  }

  getStudentById(studentId: number) {
    return this.http.get<any>(`${environment.apiUrl}/api/students/${studentId}`).pipe(take(1));
  }

  updateStudent(studentId: number | null, value: any) {
    this.isLoading = true;
    const {school,...rest} = value;
    const language = this.translateService.currentLang;
    return this.http.put<any>(`${environment.apiUrl}/api/students/${studentId}`, {language,...rest});
    this.isLoading = false;
  }

  showSnackbar(message: string){
    this.snackbar.open(message,'Fechar');
  }

  findByRegistrySimilarity(registry: any) {
    return this.http.get<any[]>(`${environment.apiUrl}/api/students/registry/${registry}`)
  }
}

import {Component, EventEmitter, Input, OnInit, Output, SimpleChanges} from '@angular/core';
import {MatTableDataSource} from "@angular/material/table";
import {TeacherService} from "../../../teacher/services/teacher.service";
import {FormBuilder} from "@angular/forms";
import {concatMap, debounceTime, filter} from "rxjs/operators";
import {StudentsService} from "../../../students/services/students.service";
import {ClassService} from "../../services/class.service";
import {TranslateService} from "@ngx-translate/core";
import {MatSnackBar} from "@angular/material/snack-bar";

@Component({
  selector: 'app-student-table',
  templateUrl: './student-table.component.html',
  styleUrls: ['./student-table.component.scss']
})
export class StudentTableComponent implements OnInit {

  @Output() next = new EventEmitter();
  @Output() previous = new EventEmitter();
  @Output() studentsChange = new EventEmitter<any>();

  @Input() clear = false;
  @Input() students:any[] = [];
  @Input() classId: any;

  editMode = false;

  studentColumns = ['name','email','actions']
  studentDataSource = new MatTableDataSource<any>([]);

  studentRegistryControl = this.fb.control('');
  studentOptions: any[] = [];

  constructor(
    private studentsService: StudentsService,
    private fb: FormBuilder,
    private classService: ClassService,
    private translateService: TranslateService,
    private snackbar: MatSnackBar
  ) { }

  ngOnInit(): void {
    this.studentRegistryControl
      .valueChanges
      .pipe(
        filter(e => e),
        filter(e => e.length > 3),
        debounceTime(1000),
        concatMap(e => {
          return this.studentsService.findByRegistrySimilarity(e);
        })
      ).subscribe(teachers => {
      this.studentOptions = teachers;
    })
  }

  ngOnChanges(changes: SimpleChanges){
    if(this.clear){
      this.studentRegistryControl.reset();
      this.studentDataSource.data = this.students;
    }else{
      this.studentRegistryControl.reset();
      this.studentDataSource.data = this.students;
    }
  }


  addTeacher(){
    this.studentsChange.emit([...this.students,this.studentRegistryControl.value])
    if(this.editMode){
      this.classService.addStudentToClass(this.studentRegistryControl.value.id,this.classId).subscribe(success => {
        this.translateService.get('messages.teacherStatusChangeSuccess').subscribe( translation => {
          this.snackbar.open(translation,'Fechar').afterDismissed();
          this.studentRegistryControl.reset();
        })
      })
    }
  }

  removeStudent(teacherId: any){
    this.studentsChange.emit([...this.students.filter(t => t.id != teacherId)]);
  }

  displayName(teacher: any){
    return teacher && teacher.name ? teacher.name: '';
  }

  isInvalid() {
    return this.students.length < 1;
  }

  emitNext(){
    this.next.emit();
  }

  emitPrevious() {
    this.previous.emit();
  }

  changeStatus(status: boolean, studentId:any) {
    this.classService.updateStudentStatus(status,this.classId,studentId).subscribe(success => {
      this.translateService.get('messages.teacherStatusChangeSuccess').subscribe( translation => {
        this.snackbar.open(translation,'Fechar').afterDismissed();
      })
    },error => {
      this.translateService.get('error.teacherStatusChangeError').subscribe( translation => {
        this.snackbar.open(translation,'Atualizar pgina').afterDismissed().subscribe(() =>{

        });
      })
    });
  }
}
<div fxLayout="row" class="full-width">
  <mat-form-field appearance="outline" fxFlex="80">
    <input
      matInput
      type="text"
      [formControl]="studentRegistryControl"
      placeholder="{{'placeholder.studentRegistry'|translate}}"
