      {{teacher.name}}
    </mat-option>
  </mat-autocomplete>
  <button mat-mini-fab color="primary" (click)="addTeacher()">
    <mat-icon>add</mat-icon>
  </button>
</div>
<table mat-table [dataSource]="studentDataSource" class="mat-elevation-z1 full-width">

  <ng-container matColumnDef="name">
