import {Component, EventEmitter, Input, OnInit, Output, SimpleChanges} from '@angular/core';
import {FormBuilder, Validators} from "@angular/forms";
import {of} from "rxjs";
import School from "../../../../shared/model/school";
import {ActivatedRoute, Router} from "@angular/router";
import {ManagerService} from "../../../../admin/manager/services/manager.service";
import {SchoolService} from "../../../../admin/schools/services/school.service";
import {MatSnackBar} from "@angular/material/snack-bar";
import {TranslateService} from "@ngx-translate/core";
import {MatTableDataSource} from "@angular/material/table";
import {TeacherService} from "../../../teacher/services/teacher.service";
import {concatMap, debounceTime, exhaustMap, filter, switchMap} from "rxjs/operators";
import {ClassService} from "../../services/class.service";

@Component({
  selector: 'app-class-form',
  templateUrl: './class-form.component.html',
  styleUrls: ['./class-form.component.scss']
})
export class ClassFormComponent implements OnInit {

  isEdit = false;

  @Input() classId:number | null = null;
  @Input() createMode = true;

  @Output() save = new EventEmitter();

  clear = false;

  teachers:any[] = [];
  students:any[] = [];
  classNameControl = this.fb.control('',Validators.required);
  isActiveControl = this.fb.control('',Validators.required);

  isLoading = false;


  constructor(
    private route: ActivatedRoute,
    private fb: FormBuilder,
    private classService: ClassService,
    private translateService: TranslateService,
    private snackbar: MatSnackBar
  ) { }

  ngOnInit(): void {
    this.route.params.subscribe(value => {
      if(value['id']){
        this.isEdit = true;
        this.classId = value['id'];

      }
    })
  }

  ngOnChanges(changes: SimpleChanges){
    this.isEdit = !this.createMode;
    if(this.createMode){
      this.teachers = [];
      this.students = [];
      this.classNameControl.reset();
      this.clear = true;
      setTimeout(() => {
        this.clear = false;
      },500)
    }
    else{
      this.classService.getClassById(this.classId).subscribe(response => {
        this.teachers = response.teachers.map((t:any) => ({isActive: t.isActive,name: t.teacher.name,email: t.teacher.email,id:t.teacher.id}));
        this.students = response.students.map((s:any) => ({isActive: s.isActive,name: s.student.name,registry: s.student.registry,id:s.student.id}));
        this.classNameControl.setValue(response.name);
        this.isActiveControl.setValue(response.isActive);
      })
    }
  }

  createClass(){
    this.isLoading = true;
    const teachersId = this.teachers.map(teacher => teacher.id);
    const studentsId = this.students.map(student => student.id);
    this.classService.createClass({
      teachersId,
      studentsId,
      className: this.classNameControl.value,
      isActive: this.isActiveControl.value
    }).subscribe( success => {
      this.translateService.get('messages.classCreated').subscribe( translation => {
        this.isLoading = false;
        this.save.emit();
        this.snackbar.open(translation,'Fechar').afterDismissed();
        this.isLoading = false;
      })
    }, error => {
      this.isLoading = false;
    })
  }

  updateStatusAndName(){
    this.isLoading = true;
    this.classService.updateClassNameAndStatus({name: this.classNameControl.value,isActive: this.isActiveControl.value},this.classId).subscribe(success => {
      this.translateService.get('messages.classNameAndStatusUpdated').subscribe( translation => {
        this.isLoading = false;
        this.save.emit();
        this.snackbar.open(translation,'Fechar').afterDismissed();
      })
    }, error => {
      this.isLoading = false;
    })
  }

  displayName(teacher: any){
    return teacher && teacher.name ? teacher.name: '';
  }

  get teachersCompleted(){
    return this.teachers.length > 0;
  }

  get studentsCompleted(){
    return this.students.length > 0;
  }
}
<div class="default-padding full-width" fxLayout="column" fxLayoutAlign="center start">

  <h1 class="title">
    {{(isEdit?'labels.editClass' : 'labels.createClass') |translate}}
  </h1>
  <mat-stepper class="full-width" orientation="vertical" [linear]="true" #stepper fxFlex="100">
    <mat-step [completed]="teachersCompleted">
      <ng-template matStepLabel>{{'labels.chooseTeachers'|translate}}</ng-template>
      <app-teacher-table
        (next)="stepper.next()"
        [(teachers)]="teachers"
        [clear]="clear"
        [classId]="classId"
        [editMode]="isEdit">
      </app-teacher-table>
    </mat-step>
    <mat-step>
      <ng-template matStepLabel>{{'labels.chooseStudents'|translate}}</ng-template>
      <app-student-table
        (previous)="stepper.previous()"
        (next)="stepper.next()"
        [(students)]="students"
        [clear]="clear"
        [classId]="classId">
      </app-student-table>
    </mat-step>
    <mat-step [completed]="studentsCompleted">
      <ng-template matStepLabel>{{'labels.chooseClassName'|translate}}</ng-template>
      <div fxLayout="row" fxFlex="100" fxLayoutAlign="space-between">
        <mat-form-field
          fxFlex="80" appearance="outline">
          <mat-label>{{'labels.className'|translate}}</mat-label>
          <input
            matInput
            type="text"
            [formControl]="classNameControl"
            placeholder="{{'placeholder.className'|translate}}"
            required>
        </mat-form-field>
        <mat-slide-toggle data-cy="isActive" [formControl]="isActiveControl" >{{'labels.active'|translate}}</mat-slide-toggle>
      </div>
      <button
        mat-flat-button
        color="primary"
        [disabled]="!teachersCompleted || !studentsCompleted || classNameControl.invalid"
        (click)="isEdit? updateStatusAndName():createClass()">{{(isEdit?'labels.save':'labels.createClass')|translate}}</button>
    </mat-step>
  </mat-stepper>
</div>

import { Component, OnInit } from '@angular/core';
import {FormControl} from "@angular/forms";
import {ManagerService} from "../../../../admin/manager/services/manager.service";
import {PageEvent} from "@angular/material/paginator";
import {ClassService} from "../../services/class.service";

@Component({
  selector: 'app-class-page',
  templateUrl: './class-page.component.html',
  styleUrls: ['./class-page.component.scss']
})
export class ClassPageComponent implements OnInit {

  isLoading = true;
  isChangingPage = false;

  classes:any[] = [];

  totalLength = 0;
  pageSize = 5;

  searchControl = new FormControl('');

  classToggleId: number | null = null;

  createMode = true;

  clear = false;

  constructor(private classService: ClassService) {
    this.refresh();
  }

  ngOnInit(): void {
  }

  refresh(){
    this.classService.getPaginatedClasses(this.pageSize,0).subscribe(response => {
      this.classes = response.content;
      this.totalLength = response.totalElements;
      this.isLoading = false;
    })
  }

  pageChange(pageEvent: PageEvent) {
    this.isChangingPage = true;
    this.classService.getPaginatedClasses(pageEvent.pageSize,pageEvent.pageIndex).subscribe(response => {
      this.classes = response.content;
      this.pageSize = pageEvent.pageSize;
      this.totalLength = response.totalElements;
      this.isChangingPage = false;
    })
  }

}
<div fxLayout="column" fxLayoutAlign="center start">
  <div class="page-title" fxFlexAlign="start" fxLayoutAlign="center start">
    <button mat-icon-button routerLink="/manager/dashboard"><mat-icon>navigate_before</mat-icon></button>
    <h1 class="mat-display-1">{{'labels.classes'|translate}}</h1>
  </div>
  <div class="full-width" fxLayout="row" fxLayoutAlign="space-around  start">
    <div fxFlex="45">
      <mat-form-field class="full-width" appearance="outline">
        <input type="tel" matInput placeholder="{{'placeholder.searchClass'|translate}}" [formControl]="searchControl">
        <mat-icon matSuffix>search</mat-icon>
      </mat-form-field>
      <a
        [ngClass]="'mat-elevation-z1 '+  (createMode?'toggled-tile':'tile')"
        fxLayout="row"
        fxFlexAlign="center center"
        fxLayoutAlign="start center"
        (click)="createMode=true;classToggleId=null;">
        <mat-icon color="primary">add</mat-icon>
        <p color="primary">{{'labels.createClass'|translate}}</p>
      </a>
      <div *ngIf="isLoading" fxLayout="row" fxLayoutAlign="center center">
        <mat-spinner></mat-spinner>
      </div>
      <a
        [ngClass]="'mat-elevation-z1 ' + (classToggleId===classroom.id?'toggled-tile':'tile')"
        fxLayout="row"
        fxFlexAlign="center center"
        fxLayoutAlign="start center"
        *ngFor="let classroom of classes"
        (click)="classToggleId=classroom.id;createMode=false">
        <mat-icon [ngClass]="classroom.isActive?'primary':'disabled'">person</mat-icon>
        <p color="primary">{{classroom.name}}</p>
      </a>
      <mat-paginator
        class="full-width"
        [length]="totalLength"
        [pageSize]="pageSize"
        [pageSizeOptions]="[5, 10, 25, 100]"
        (page)="pageChange($event)">
      </mat-paginator>
    </div>
    <div fxFlex="45" fxFlexAlign="start">
      <app-class-form
        *ngIf="classToggleId || createMode"
        [classId]="classToggleId"
        [createMode]="createMode"
        (save)="refresh()">
      </app-class-form>
    </div>
  </div>
</div>

import {Component, EventEmitter, Input, OnInit, Output, SimpleChanges} from '@angular/core';
import {FormBuilder, Validators} from "@angular/forms";
import {of} from "rxjs";
import School from "../../../../shared/model/school";
import {ActivatedRoute, Router} from "@angular/router";
import {TeacherService} from "../../../teacher/services/teacher.service";
import {SchoolService} from "../../../../admin/schools/services/school.service";
import {MatSnackBar} from "@angular/material/snack-bar";
import {TranslateService} from "@ngx-translate/core";
import {StudentsService} from "../../services/students.service";

@Component({
  selector: 'app-students-form',
  templateUrl: './students-form.component.html',
  styleUrls: ['./students-form.component.scss']
})
export class StudentsFormComponent implements OnInit {


  isEdit = false;

  @Input() teacherId:number | null = null;
  @Input() createMode = true;

  @Output() save = new EventEmitter();

  alreadyRegisteredEmail = false;

  studentForm = this.fb.group({
    email: ['',Validators.email],
    registry: ['',Validators.required],
    name: ['',Validators.required],
    isActive:['']
  });

  filteredSchools$ = of([] as School[]);

  isLoading = false;

  constructor(
    private route: ActivatedRoute,
    private fb: FormBuilder,
    private studentsService: StudentsService,
    private schoolService: SchoolService,
    private snackbar: MatSnackBar,
    private translateService: TranslateService,
    private router: Router
  ) { }

  ngOnInit(): void {
    this.route.params.subscribe(value => {
      if(value['id']){
        this.isEdit = true;
        this.teacherId = value['id'];
        this.studentsService.getStudentById(this.teacherId as number).subscribe(({email,name,isActive,registry})=>{
          this.studentForm.setValue({email,name,isActive,registry});
        });
      }
    })
  }

  ngOnChanges(changes: SimpleChanges){
    this.isEdit = !this.createMode;
    if(this.createMode){
      this.studentForm.reset();
    }
    else{
      this.studentsService.getStudentById(this.teacherId as number).subscribe( ({name,email,isActive,registry}) => {
        this.studentForm.setValue({name,email,isActive,registry})
      })
    }
  }

  updateTeacher(){
    this.isLoading = true;
    this.studentsService.updateStudent(this.teacherId,this.studentForm.value).subscribe(success => {
      this.translateService.get('messages.studentUpdated').subscribe( translation => {
        this.isLoading = false;
        this.save.emit();
        this.snackbar.open(translation,'Fechar').afterDismissed();
      })
    }, error => {
      this.isLoading = false;
    })
  }

  registerTeacher(){
    this.isLoading = true;
    this.studentsService.registerStudent(this.studentForm.value).subscribe(success => {
      this.translateService.get('messages.studentRegistered').subscribe( translation => {
        this.isLoading = false;
        this.save.emit();
        this.studentForm.reset();
        this.snackbar.open(translation,'Fechar');
      })
    }, error => {
      this.isLoading = false;
      switch (error.status){
        case 409:
          this.alreadyRegisteredEmail = true;
          break;
      }
    });
  }

  showSnackbar(message: string){
    this.snackbar.open(message,'Fechar');
  }

}
